"use client"
import { me } from "../../apisectionH/apiH"
import { useQuery } from "@tanstack/react-query"

const Page = () => {
  const { data, isLoading } = useQuery({
    queryKey: ["me"],
    queryFn: me,
  });

  if (isLoading) return <p className="text-white text-center">Loading...</p>;

  return (
    <div className=" flex items-center justify-center px-4 bg-[blue]">
         <div className="bg-[yellow] p-4  w-[70vw] gap-2 border-2 border-white flex flex-col items-center justify-center rounded-2xl shadow-2xl">
         <div>
            <img className="h-20 w-20 rounded-full" src={
                data?.profilepic?"":
                `data:image/png;base64,`
            }/>
         </div>
             <p>{data?.fullname}</p>
             <p>{data?.email}</p>

             
            <form className="w-1/3 bg-[yellow]">
                 
            </form>
         </div>
    </div>
  );
};

export default Page;
